import React, { useState, FC } from 'react'

interface IPost {
    title: string;
    text: string;
    date: string;
    img: string;
    id: number;
}

interface IPosts {
    id: number;
    image: string;
    text: string;
    date: string;
    lesson_num: number;
    title: string;
    description: string;
    author: number;
}

const Post: FC<IPost> = ({title, text, date, img}) => {
    const [arrPost, setArrPost] = useState<IPosts[]>([]);

    const fetchPost = async () => {
      const response = await fetch('https://studapi.teachmeskills.by/blog/posts/?limit=10');
      const data = await response.json();
      const results = data.results;
      const posts = results.slice(0, 10);
      setArrPost(posts);
    }
    return (
        <div className='btn'>
          <button onClick={fetchPost}>Load Posts</button>
          {arrPost.map((item) => (
            <div key={item.title}>
              <div>{item.title}</div>
              {item.text ? <div>{item.text}</div> : null}
              <div>{item.date}</div>
              <img src={item.img} alt="icon" />
            </div>
          ))}
        </div>
      );
}

export default Post
