import React, { useState, FC } from 'react'
import './style.css'

interface IPost {
    title: string;
}

interface IPosts {
    // id: number;
    text: string;
    date: string;
    title: string;
}

const Post = ({data}:IPosts) => {
    return (
        <div key={data.id} className='post__item post__big'>
            <div>{data.date}</div>
            <div>{data.title}</div>
            {data.text ? <div>{data.text}</div> : null}
        </div> 
    )
}

const MyComponent = () => {
    const [posts, setArrPost] = useState<IPosts[]>([]);

    const fetchPost = async () => {
        const response = await fetch('https://studapi.teachmeskills.by/blog/posts/?limit=10');
        const data = await response.json();
        const results = data.results;
        const posts = results.slice(0, 10);
        setArrPost(posts);
    };

    return (
        <>
        <button onClick={fetchPost} className='btnPost'>Load Posts</button>
        {posts.map(({title, text, date, id}) => (
            <Post key={id} title={title} text={text} date={date}/>
        ))}
        </>
        
    )
}

