import React, { useState, FC } from 'react'
import './style.css'

interface IPost {
    title: string;
    // text: string;
    // date: string;
    // img: string;
    // id: number;
}

interface IPosts {
    id: number;
    text: string;
    date: string;
    // lesson_num: number;
    title: string;
    // description: string;
    // author: number;
}

const Post: FC<IPost> = ({title}) => {
    const [arrPost, setArrPost] = useState<IPosts[]>([]);

    const fetchPost = async () => {
      const response = await fetch('https://studapi.teachmeskills.by/blog/posts/?limit=10');
      const data = await response.json();
      const results = data.results;
      const posts = results.slice(0, 10);
      setArrPost(posts);
    }
    return (
        <>
            <button onClick={fetchPost}>Load Posts</button>
            <div className='post__BIG__items'>
                {arrPost.map((item) => (
                <>
                    {item.id < 2 ? 
                        <div key={item.id} className='post__item'>
                            <div>{item.date}</div>
                            <div>{item.title}</div>
                            {item.text ? <div>{item.text}</div> : null}
                        </div> 
                        : ''
                    
                    }
                    
                </>
            ))}
            </div>
            <div className='post__MID__items'>
                {arrPost.map((item) => (
                <>
                    {item.id > 1 && item.id < 6 ? 
                        <div key={item.id} className='post__item'>
                        <div>{item.date}</div>
                        <div>{item.title}</div>
                        {item.text ? <div>{item.text}</div> : null}
                        </div> 
                        : ''
                    }
                    {/* <div key={item.id} className='post__item'>
                        <div>{item.date}</div>
                        <div>{item.title}</div>
                        {item.text ? <div>{item.text}</div> : null}
                    </div> */}
                </>
            ))}
            </div>
            <div className='post__SMALL__items'>
                {arrPost.map((item) => (
                <>
                    {item.id > 5 ? console.log(item + 's') : ''}
                </>
            ))}
            </div>

        </>
      );
}

export default Post
