import React, { useState, FC } from 'react'

interface IPost {
    title: string;
    // text: string;
    // date: string;
    // img: string;
    // id: number;
}

interface IPosts {
    id: number;
    text: string;
    date: string;
    // lesson_num: number;
    title: string;
    // description: string;
    // author: number;
}

const Post: FC<IPost> = ({title}) => {
    const [arrPost, setArrPost] = useState<IPosts[]>([]);

    const fetchPost = async () => {
      const response = await fetch('https://studapi.teachmeskills.by/blog/posts/?limit=10');
      const data = await response.json();
      const results = data.results;
      const posts = results.slice(0, 10);
      setArrPost(posts);
    }
    return (
        <div className='btn'>
          <button onClick={fetchPost}>Load Posts</button>
          {arrPost.map((item) => (
            <div className={`post${item.id}`}>
                <div key={item.id}>
                <div>{item.title}</div>
                {item.text ? <div>{item.text}</div> : null}
                <div>{item.date}</div>
            </div>
                
            </div>
          ))}
        </div>
      );
}

export default Post
