import React, { useState, FC } from 'react'
import './style.css'

interface IPost {
    title: string;
    // text: string;
    // date: string;
    // img: string;
    // id: number;
}

interface IPosts {
    id: number;
    text: string;
    date: string;
    // lesson_num: number;
    title: string;
    // description: string;
    // author: number;
}

const Post: FC<IPost> = ({title}) => {
    const [arrPost, setArrPost] = useState<IPosts[]>([]);

    const fetchPost = async () => {
      const response = await fetch('https://studapi.teachmeskills.by/blog/posts/?limit=10');
      const data = await response.json();
      const results = data.results;
      const posts = results.slice(0, 10);
      setArrPost(posts);
    }
    return (
        <>
            <button onClick={fetchPost}>Load Posts</button>
            <div className='btn'>
                {arrPost.map((item) => (
                <div key={item.id} className='post'>
                <div>{item.title}</div>
                {item.text ? <div>{item.text}</div> : null}
                <div>{item.date}</div>
                </div>
            ))}
            </div>
        </>
      );
}

export default Post
