import React, { useState, FC } from 'react'
import './style.css'

interface IPost {
    title: string;
}

interface IPosts {
    id: number;
    text: string;
    date: string;
    title: string;
}


const Post: FC<IPost> = ({title}) => {
    const [arrPost, setArrPost] = useState<IPosts[]>([]);

    const fetchPost = async () => {
      const response = await fetch('https://studapi.teachmeskills.by/blog/posts/?limit=10');
      const data = await response.json();
      const results = data.results;
      const posts = results.slice(0, 10);
      setArrPost(posts);
    }
    return (
        <>
            <button onClick={fetchPost} className='btnPost'>Load Posts</button>
            <div className='post__items'>
                <div className='post__left'>
                    <div className='post__BIG'>
                {arrPost.map((item) => (
                <>
                    {item.id < 2 ? 
                        <div key={item.id} className='post__item post__big'>
                            <div>{item.date}</div>
                            <div>{item.title}</div>
                            {item.text ? <div>{item.text}</div> : null}
                        </div> 
                        : ''
                    }
                </>
            ))}
                    </div>
                    <div className='post__MID'>
                        {arrPost.map((item) => (
                            <>
                            {item.id > 1 && item.id < 6 ? 
                            <div key={item.id} className='post__item post__mid'>
                                <div>{item.date}</div>
                                <div>{item.title}</div>
                                {item.text ? <div>{item.text}</div> : null}
                            </div> 
                            : ''
                            }
                            </>
                        ))}
                    </div>
                </div>
            <div className='post__right'>
                {arrPost.map((item) => (
                <>
                    {item.id > 5 ? 
                        <div key={item.id} className='post__item post__small'>
                            <div>{item.date}</div>
                            <div>{item.title}</div>
                            {item.text ? <div>{item.text}</div> : null}
                        </div> 
                        : ''}
                </>
            ))}
            </div>
            </div>


        </>
      );
}

export default Post
