import React, { useState, FC } from 'react'
import './style.css'

interface IPost {
    title: string;
}

interface IPosts {
    id: number;
    text: string;
    date: string;
    title: string;
}

const MyComponent = () => {
    const [posts, setArrPost] = useState<IPosts[]>([]);

    const fetchPost = async () => {
        const response = await fetch('https://studapi.teachmeskills.by/blog/posts/?limit=10');
        const data = await response.json();
        const results = data.results;
        // const posts = results.slice(0, 10);
        setArrPost(results);
    };

    return (
        <>
        <button onClick={fetchPost} className='btnPost'>Load Posts</button>
        <div className='post__items big__items'>
            {posts.map(({title, text, date, id}) => (
                id < 2 ? <Post key={id} title={title} text={text} date={date} id={id}/> : ''
            ))}
        </div>
        <div className='post__items mid__items'>
            {posts.map(({title, text, date, id}) => (
                id > 1 && id < 6 ? <Post key={id} title={title} text={text} date={date} id={id}/> : ''
            ))}
        </div>
        <div className='post__items smal__items'>
            {posts.map(({title, text, date, id}) => (
                id > 5 ? <Post key={id} title={title} text={text} date={date} id={id}/> : ''
            ))}
        </div>
        </>
    )
}

const Post:FC<IPosts> = ({title, text, date, id}) => {
    return (
        <div className='post__item'>
            <div>{date}</div>
            <div>{title}</div>
            {text ? <div>{text}</div> : null}
        </div> 
    )
}

export default MyComponent
