import React, { FC } from 'react'

interface IPost {
    title: string;
    text: string;
    date: string;
    img: string;
    id: number;
}

interface IPosts {
    id: number;
    image: string;
    text: string;
    date: string;
    lesson_num: number;
    title: string;
    description: string;
    author: number;
}

const Post: FC<IPost> = ({title, text, date, img}) => {
    let arrPost: IPosts[] = []
    const fetchPost = async () => {
        let a = await fetch(`https://studapi.teachmeskills.by/blog/posts/?limit=10`)
        let b = await a.json()
        let c = b.results
        for(let i = 0; i < 10; i++) {
            arrPost.push(c[i]);
        }  
        arrPost.map((item) => {
            <div>
                <div>{title='j'}</div>;
                {text ? <div>{text}</div> : null};
                <div>{date}</div>;
                <img src={img} alt="icon" />;
            </div>
            
            
        })
        return arrPost
        
    }
    // fetchPost();
    return (
        <div className='btn'>
            <button onClick={fetchPost}>xxx</button>
            {arrPost.map((item)=>{
                console.log(item)
                
            })}
            <div>{title='j'}</div>
            {text ? <div>{text}</div> : null}
            <div>{date}</div>
            <img src={img} alt="icon" />
        </div>
    )
}

export default Post
