import React, { FC, useState } from 'react'
import './style.css'

interface IInput {
    type: 'password' | 'text';
    placeholder?: string;
    label: string;
    onChange: (value: string) => void;
}

const Input: FC<IInput> = ({type, placeholder, label, onChange}) => {
  return (
    <div className='formInput'>
        <label>{label}</label>
        <input type={type} placeholder={placeholder} onChange={(e) => onChange(e.currentTarget.value)}/>
    </div>
  )
}

export default Input
