import React, { useState, FC } from 'react'
import image from '../../assets/image.jpg'
import './style.css'

interface IPost {
    title: string;
}

interface IPosts {
    id: number;
    text: string;
    date: string;
    title: string;
    img: string;
}

const MyComponent = () => {
    const [posts, setArrPost] = useState<IPosts[]>([]);

    const fetchPost = async () => {
        const response = await fetch('https://studapi.teachmeskills.by/blog/posts/?limit=10');
        const data = await response.json();
        const results = data.results;
        setArrPost(results);
    };

    return (
        <>
        <button onClick={fetchPost} className='post__btn'>Load Posts</button>
        <div className='post__items'>
            <div className='post__item__left'>
                <div className='post__big'>
                    {posts.map(({title, text, date, img, id}) => (
                        id < 2 ? <Post key={id} title={title} text={text} date={date} img={img} id={id}/> : ''
                    ))}
                </div>
                <div className='post__mid'>
                    {posts.map(({title, text, date, img, id}) => (
                        id > 1 && id < 6 ? <Post key={id} title={title} text={text} date={date} img={img} id={id}/> : ''
                    ))}
                </div>
            </div>
            <div className='post__small'>
                {posts.map(({title, text, date, img, id}) => (
                    id > 5 ? <Post key={id} title={title} text={text} date={date} img={img} id={id}/> : ''
                ))}
            </div>
        </div>
        </>
    )
}

const Post:FC<IPosts> = ({title, text, date}) => {
    return (
        <div className='post'>
            <p className='post__date'>{date}</p>
            <div className='post__title'>{title}</div>
            {text ? <div className='post__text'>{text}</div> : null}
            <img src={image} alt="image" />
        </div> 
    )
}

export default MyComponent
