import React, { FC } from 'react'

interface IPost {
    title: string;
    text: string;
    date: string;
    img: string;
    id: number;
}

interface IPosts {
    id: number;
    image: string;
    text: string;
    date: string;
    lesson_num: number;
    title: string;
    description: string;
    author: number;
}

const Post: FC<IPost> = ({title, text, date, img}) => {
    let arrPost: IPosts[] = []
    const fetchPost = () => {
        fetch(`https://studapi.teachmeskills.by/blog/posts/?limit=10`)
        .then((response) => response.json())
        .then(({results}) => results.map((item: IPosts) => {
            arrPost.push(item)
        }))
        console.log(arrPost);
        
    }
    console.log(fetchPost());
    return (
        <div>
            <div>{title}</div>
            {text ? <div>{text}</div> : null}
            <div>{date}</div>
            <img src={img} alt="icon" />
        </div>
    )
}

export default Post
