import React, { useState } from 'react'
import Post from '../Post'

interface IPosts {
    // id: number;
    text: string;
    date: string;
    title: string;
}
    
const PostList = () => {
    const [posts, setArrPost] = useState<IPosts[]>([]);

    const fetchPost = async () => {
        const response = await fetch('https://studapi.teachmeskills.by/blog/posts/?limit=10');
        const data = await response.json();
        const results = data.results;
        const posts = results.slice(0, 10);
        setArrPost(posts);
    };

    return (
        <>
        <button onClick={fetchPost} className='btnPost'>Load Posts</button>
        {posts.map(({title, text, date}) => (
            <Post title={title} text={text} date={date}/>
        ))}
        </>
        
    )
    
//   return (
//     <div className='post__container'>
//         <Post/>
//     </div>
    
//   )
}


export default PostList
