import React, { useState, FC } from 'react'
import image from '../../assets/image.jpg'
import './style.css'
import { IPost } from 'src/App'
import { fetchPost } from 'src/App'
import { posts } from 'src/App'




const MyComponent = () => {

    return (
        <>
        <button onClick={fetchPost} className='post__btn'>Load Posts</button>
        <div className='post__items'>
            <div className='post__item__left'>
                <div className='post__big'>
                    {posts.map(({title, text, date, img, id}) => (
                        id < 2 ? <PostBig key={id} title={title} text={text} date={date} img={img} id={id}/> : ''
                    ))}
                </div>
                <div className='post__mid'>
                    {posts.map(({title, text, date, img, id}) => (
                        id > 1 && id < 6 ? <PostMid key={id} title={title} date={date} img={img} id={id}/> : ''
                    ))}
                </div>
            </div>
            <div className='post__small'>
                {posts.map(({title, text, date, img, id}) => (
                    id > 5 ? <PostSmall key={id} title={title} date={date} img={img} id={id}/> : ''
                ))}
            </div>
        </div>
        </>
    )
}

const PostBig:FC<IPost> = ({title, text, date}) => {
    return (
        <div className='post'>
            <div className='post__content'>
                <p className='post__date'>{date}</p>
                <p className='post__title'>{title}</p>
                {text ? <p className='post__text'>{text}</p> : null}
            </div>
            <div className='post__image'>
                <img src={image} alt="image" />
            </div>
        </div> 
    )
}
const PostMid:FC<IPost> = ({title, date}) => {
    return (
        <div className='post'>
            <div className='post__content'>
                <img src={image} alt="image" />
            <p className='post__date'>{date}</p>
            <p className='post__title'>{title}</p>

            </div>
        </div> 
    )
}
const PostSmall:FC<IPost> = ({title, date}) => {
    return (
        <div className='post'>
            <div className='post__content'>
                <p className='post__date'>{date}</p>
                <p className='post__title'>{title}</p>
            </div>
            <div className='post__image'>
                <img src={image} alt="image" />
            </div>
        </div> 
    )
}

export default MyComponent
